sdram_active = get_register('register/hif_io_ctrl/hif_io_cfg/en_hif_sdram_mi') if sdram_active!=0: hif_a_width = get_register('register/hif_io_ctrl/hif_io_cfg/sel_hif_a_width') ulRows = get_register('register/hif_sdram_ctrl/sdram_general_ctrl/rows') ulRowAddr = ulRows + 10 ;# 10: uses A0-A10 11: uses A0-A11 etc. ulColumns = get_register('register/hif_sdram_ctrl/sdram_general_ctrl/columns') ulColumnAddr = ulColumns + 7 ;# 7: uses A0-A7, 8: uses A0-A8 etc.
print('Rows: %d Columns %d hif_a_width: %d' % (ulRowAddr, ulColumnAddr, hif_a_width))
if hif_a_width == 1 and (ulRowAddr >= 12 or ulColumnAddr >= 12): raise Exception('Mismatch between hif_a_width and row/column count! SDRAM row/column count requires A12 line but hif_io_cfg.hif_a_width does not select it.')
